var geocoder = new GoogleGeocoder();
var result = geocoder.GetDirections("Bratislava, Slovakia", "Trnava, Slovakia");
if (result.Status == GeocodeStatus.OK && result.Directions != null)
{
	foreach (var routeLine in result.Directions.Routes.Select(route => new Polyline(route)))
	{
		map.Overlays.Add(routeLine);
	}
}

void Map_ZoomChanged(object sender, ZoomEventArgs e)
{
    e.MapCommand = e.Map.ClearOverlays();
}

Map.PostRenderScript = Map.CreateMapCallback("new Date()", true);
Map.ExternalCallback += new ExternalCallbackHandler(Map_ExternalCallback);
void Map_ExternalCallback(string argument, ref string MapCommand)
{
    MapCommand = "alert('" + argument + "');";
}

