void Map_MapLoaded(object sender, BoundsEventArgs e)
{
    foreach (DataRow dr in dt.Rows)
    {
        double lat = (double)dr["Lat"];
        double lng = (double)dr["Lng"];
        if (lat < Bounds.MaxLatitude && lat > Bounds.MinLatitude && lng < Bounds.MaxLongitude && lng > Bounds.MinLongitude)
        {
            e.MapCommand = e.Map.AddOverlay(new Marker((string)dr["ID"], lat, lng), true);
        }
    }
}

void Map_Click(object sender, CoordinatesEventArgs e)
{
    Marker marker = new Marker("ID", e.Position);
    e.MapCommand = e.Map.AddOverlay(marker, true);
}

private void Map_OverlayClick(object sender, OverlayEventArgs e)
{
    if (e.Overlay is Marker)
    {
        e.MapCommand = ((Marker)e.Overlay).OpenInfoWindowHTML(e.Map, "You clicked me");
    }
}

void GMap_RightClick(object sender, PixelEventArgs e)
{
    Marker marker = new Marker(DateTime.Now.Millisecond.ToString(), e.Position);
    e.MapCommand = e.Map.AddOverlay(marker, true);
}

DataTable dt = new DataTable();
//Dummy polygon for the sake of example.
Polygon polygon = new Polygon();
//Set up the database connection.
System.Data.SqlClient.SqlConnection conn = new System.Data.SqlClient.SqlConnection("Your connection string");
System.Data.SqlClient.SqlCommand cmd = new System.Data.SqlClient.SqlCommand(
    @"SELECT ID, Lat, Lng, Text FROM Overlays 
    WHERE Lat < @MaxLat AND Lat > @MinLat AND Lng < @MaxLng AND Lng > @MinLng",
    conn);
cmd.Parameters.AddWithValue("@MaxLat", polygon.Bounds.MaxLatitude);
cmd.Parameters.AddWithValue("@MinLat", polygon.Bounds.MinLatitude);
cmd.Parameters.AddWithValue("@MaxLng", polygon.Bounds.MaxLongitude);
cmd.Parameters.AddWithValue("@MinLng", polygon.Bounds.MinLongitude);
conn.Open();
SqlDataReader dtr = cmd.ExecuteReader();
while (dtr.Read())
{
    Marker marker = new Marker();
    marker.theID = (string)dr["ID"];
    marker.Latitude = (double)dr["Lat"];
    marker.Longitude = (double)dr["Lng"];
    marker.MarkerText = (string)dr["Text"];
    Map.Overlays.Add(marker);
}
dtr.Close();
conn.Close();

